import React, { Component } from 'react';

import PropTypes from 'prop-types';
import { Card, Image, Rate, Spin } from 'antd';
import { format } from 'date-fns';
import MovieGenres from '../movie-genres';
import MovieTitle from '../movie-title/movie-title';

import './movie-card.css';

const { Meta } = Card;

export default class MovieCard extends Component {
    static propTypes = {
        rateMovie: PropTypes.func,
        setNotice: PropTypes.func.isRequired,
        movie: PropTypes.shape({
            id: PropTypes.number,
            title: PropTypes.string,
            poster_path: PropTypes.string,
            release_date: PropTypes.string,
            genre_ids: PropTypes.arrayOf(PropTypes.number),
            overview: PropTypes.string,
            vote_average: PropTypes.number,
            rating: PropTypes.number,
        }).isRequired,
    };

    static defaultProps = {
        rateMovie: null,
    };

    state = {
        rating: 0,
        loader: false,
    };

    constructor(props) {
        super(props);

        const {
            movie: { rating = null },
        } = props;
        if (rating) this.state.rating = rating;

        this.descRef = React.createRef();
        this.descBoxRef = React.createRef();
    }

    componentDidMount() {
        this.cropText();
    }

    onChangeRate = (rating) => {
        const {
            rateMovie,
            movie: { id },
        } = this.props;

        if (!rating || !rateMovie) return false;

        this.setState({ loader: true });

        rateMovie(id, rating)
            .then(() => {
                this.setState({
                    rating,
                    loader: false,
                });
            })
            .catch(({ message }) => {
                const { setNotice } = this.props;

                this.setState({ loader: false });

                setNotice({
                    type: 'error',
                    message,
                });
            });

        return true;
    };

    cropText = () => {
        const descBox = this.descBoxRef.current;
        const desc = this.descRef.current;

        while (descBox.clientHeight < desc.clientHeight) {
            const descArr = desc.textContent.split(' ');

            desc.textContent = `${descArr.slice(0, descArr.length - 2).join(' ')} ...`;
        }
    };

    render() {
        const {
            movie: {
                release_date: date = null,
                overview: desc = '',
                genre_ids: genreIds = [],
                poster_path: poster = null,
                title = '',
                vote_average: voteAverage = 0,
            },
        } = this.props;

        const { rating, loader } = this.state;

        const formatDate = date ? format(new Date(date), 'MMMM d, uuuu') : 'Release date not specified';

        const imgSrc = poster ? `https://image.tmdb.org/t/p/w500${poster}` : '';

        return (
            <Spin wrapperClassName="movie-card" size="large" spinning={loader} tip="Setting a rating for a movie...">
                <Card className="movie-card__card">
                    <Image
                        className="movie-card__img"
                        alt={title}
                        src={imgSrc}
                        fallback="data:image/png;base64,"
                    />
                    <Meta
                        className="movie-card__meta"
                        title={<MovieTitle title={title} voteAverage={voteAverage} />}
                        description={
                            <div direction="vertical" size={[7]}>
                                <p className="movie-card__date">{formatDate}</p>
                                <div className="movie-card__tags">
                                    <MovieGenres genreIds={genreIds} />
                                </div>
                                <div ref={this.descBoxRef} className="movie-card__desc">
                                    <p ref={this.descRef}>{desc}</p>
                                </div>
                                <Rate
                                    style={{ fontSize: 15 }}
                                    count={10}
                                    value={rating}
                                    onChange={this.onChangeRate}
                                    allowHalf
                                    disabled={Boolean(rating)}
                                />
                            </div>
                        }
                    />
                </Card>
            </Spin>
        );
    }
}
